import React, { useState, useEffect, useRef } from 'react';
import { Github, Linkedin, Mail, ExternalLink, Code, Rocket, Cloud, Brain, Terminal, Zap, Star, Eye, TrendingUp, ChevronDown, Menu, X, Youtube, Instagram, BookOpen, Activity, GitFork, Sun, Moon, Award, Coffee, Users, Briefcase, Calendar, MapPin, Phone, Send, Clock, CheckCircle, ArrowRight, Download, Play, Pause } from 'lucide-react';

const Portfolio = () => {
  const [darkMode, setDarkMode] = useState(true);
  const [activeSection, setActiveSection] = useState('home');
  const [menuOpen, setMenuOpen] = useState(false);
  const [githubStats, setGithubStats] = useState(null);
  const [repos, setRepos] = useState([]);
  const [contributions, setContributions] = useState(0);
  const [mediumPosts, setMediumPosts] = useState([]);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [scrollY, setScrollY] = useState(0);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({ name: '', email: '', message: '', type: 'consultation' });
  const [formStatus, setFormStatus] = useState('');
  const [activeTestimonial, setActiveTestimonial] = useState(0);
  const [counters, setCounters] = useState({ projects: 0, clients: 0, hours: 0, rating: 0 });

  // Animated counters
  useEffect(() => {
    const targets = { projects: 50, clients: 100, hours: 5000, rating: 4.9 };
    const duration = 2000;
    const steps = 60;
    const interval = duration / steps;

    let step = 0;
    const timer = setInterval(() => {
      step++;
      setCounters({
        projects: Math.floor((targets.projects / steps) * step),
        clients: Math.floor((targets.clients / steps) * step),
        hours: Math.floor((targets.hours / steps) * step),
        rating: ((targets.rating / steps) * step).toFixed(1)
      });
      if (step >= steps) clearInterval(timer);
    }, interval);

    return () => clearInterval(timer);
  }, []);

  // GitHub data
  useEffect(() => {
    const fetchGitHub = async () => {
      try {
        const [userRes, reposRes, eventsRes] = await Promise.all([
          fetch('https://api.github.com/users/vivekjha1213'),
          fetch('https://api.github.com/users/vivekjha1213/repos?sort=updated&per_page=100'),
          fetch('https://api.github.com/users/vivekjha1213/events/public?per_page=100')
        ]);

        const userData = await userRes.json();
        const reposData = await reposRes.json();
        const eventsData = await eventsRes.json();

        const totalStars = reposData.reduce((acc, repo) => acc + (repo.stargazers_count || 0), 0);
        const totalForks = reposData.reduce((acc, repo) => acc + (repo.forks_count || 0), 0);
        const pushEvents = eventsData.filter(e => e.type === 'PushEvent').length;

        setGithubStats({
          repos: userData.public_repos || 0,
          followers: userData.followers || 0,
          following: userData.following || 0,
          stars: totalStars,
          forks: totalForks,
          contributions: pushEvents
        });

        setRepos(reposData.slice(0, 6));
        setLoading(false);
      } catch (error) {
        console.error('GitHub fetch error:', error);
        setLoading(false);
      }
    };

    fetchGitHub();
  }, []);

  // Medium posts
  useEffect(() => {
    const fetchMedium = async () => {
      try {
        const res = await fetch('https://api.rss2json.com/v1/api.json?rss_url=https://medium.com/feed/@vivekjha1213');
        const data = await res.json();
        setMediumPosts(data.items?.slice(0, 6) || []);
      } catch (error) {
        console.error('Medium fetch error:', error);
      }
    };

    fetchMedium();
  }, []);

  // Mouse tracking
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    const handleScroll = () => setScrollY(window.scrollY);

    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Auto-rotate testimonials
  useEffect(() => {
    const timer = setInterval(() => {
      setActiveTestimonial(prev => (prev + 1) % testimonials.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    setFormStatus('sending');

    setTimeout(() => {
      setFormStatus('success');
      setFormData({ name: '', email: '', message: '', type: 'consultation' });
      setTimeout(() => setFormStatus(''), 3000);
    }, 1500);
  };

  const skills = [
    { icon: 'ðŸ’»', name: 'GoLang', level: 95, color: 'from-cyan-500 to-blue-500' },
    { icon: 'ðŸ', name: 'Python', level: 90, color: 'from-green-500 to-emerald-500' },
    { icon: 'â˜ï¸', name: 'AWS/Cloud', level: 88, color: 'from-orange-500 to-red-500' },
    { icon: 'ðŸ³', name: 'Docker/K8s', level: 92, color: 'from-blue-500 to-indigo-500' },
    { icon: 'ðŸ¤–', name: 'AI/ML', level: 85, color: 'from-purple-500 to-pink-500' },
    { icon: 'âš¡', name: 'Microservices', level: 93, color: 'from-yellow-500 to-amber-500' },
    { icon: 'ðŸ—„ï¸', name: 'Databases', level: 89, color: 'from-red-500 to-pink-500' },
    { icon: 'ðŸš', name: 'Drone Tech', level: 87, color: 'from-indigo-500 to-violet-500' }
  ];

  const techStack = {
    'Languages': ['GoLang', 'Python', 'Java', 'JavaScript', 'TypeScript', 'C++', 'SQL'],
    'Backend': ['FastAPI', 'Django', 'Flask', 'Node.js', 'gRPC', 'GraphQL', 'REST APIs'],
    'Databases': ['PostgreSQL', 'MySQL', 'MongoDB', 'Redis', 'DynamoDB', 'Elasticsearch'],
    'Cloud & DevOps': ['AWS', 'Docker', 'Kubernetes', 'Terraform', 'Jenkins', 'GitHub Actions', 'CI/CD'],
    'Message Queues': ['Apache Kafka', 'RabbitMQ', 'AWS SQS', 'Redis Pub/Sub'],
    'AI/ML': ['LangChain', 'OpenAI API', 'TensorFlow', 'PyTorch', 'Scikit-learn'],
    'Frontend': ['React', 'Next.js', 'Tailwind CSS', 'TypeScript'],
    'Tools': ['Git', 'Linux', 'Nginx', 'Postman', 'Grafana', 'Prometheus']
  };

  const freelanceServices = [
    {
      title: 'Custom Software Development',
      description: 'Full-stack applications, APIs, microservices architecture',
      price: 'From $50/hr',
      features: ['Scalable Architecture', 'Performance Optimization', 'Cloud Deployment', 'Maintenance Support']
    },
    {
      title: 'DevOps & Cloud Solutions',
      description: 'AWS infrastructure, CI/CD pipelines, containerization',
      price: 'From $60/hr',
      features: ['AWS Setup', 'Docker/K8s', 'Automation', '99.9% Uptime']
    },
    {
      title: 'AI/ML Integration',
      description: 'AI chatbots, automation, data analysis solutions',
      price: 'From $70/hr',
      features: ['Custom AI Models', 'API Integration', 'Training & Support', 'Real-time Processing']
    },
    {
      title: 'Technical Consulting',
      description: 'Architecture review, code optimization, mentoring',
      price: 'From $80/hr',
      features: ['System Design', 'Performance Audit', 'Team Training', 'Best Practices']
    }
  ];

  const testimonials = [
    {
      name: 'ISRO Project Lead',
      role: 'Indian Space Research Organization',
      text: 'Vivek delivered exceptional CLI tools that streamlined our data processing workflows. Professional, reliable, and technically brilliant.',
      rating: 5
    },
    {
      name: 'Sarah Johnson',
      role: 'CTO, TechCorp',
      text: 'His microservices architecture scaled our platform from 10K to 1M+ users seamlessly. Best investment we made!',
      rating: 5
    },
    {
      name: 'Forest Department Head',
      role: 'Government Agency',
      text: 'Desktop application reduced our manual work by 70%. Outstanding developer with great communication skills.',
      rating: 5
    }
  ];

  const experiences = [
    {
      company: 'Vyturr Tech Innovations',
      role: 'Software Engineer (SDE-2)',
      location: 'Singapore ðŸ‡¸ðŸ‡¬',
      period: 'Jan 2025 - Present',
      logo: 'ðŸ›¡ï¸',
      highlights: [
        'Architected cross-platform compliance agent processing 10K+ daily scans',
        'Optimized PostgreSQL & Redis achieving 60% performance boost',
        'Built real-time chat with WebSocket reducing response time by 45%',
        'AWS infrastructure maintaining 99.9% uptime with auto-scaling'
      ]
    },
    {
      company: 'Chat360',
      role: 'Software Engineer (SDE-1)',
      location: 'Pune ðŸ‡®ðŸ‡³',
      period: 'Oct 2023 - Jan 2025',
      logo: 'ðŸ’¬',
      highlights: [
        'Designed microservices handling 1M+ messages daily',
        'Boosted throughput from 500 to 850 req/sec with Redis & RabbitMQ',
        'Developed AI voice bot supporting 5K+ concurrent chats',
        'Managed infrastructure serving 500K+ users'
      ]
    },
    {
      company: 'Freelance Developer',
      role: 'Technical Consultant & Pro Coder',
      location: 'Global ðŸŒ',
      period: '2022 - Present',
      logo: 'ðŸ’¼',
      highlights: [
        'Built custom solutions for ISRO and Forest Department',
        'Mentored 100+ students & professionals',
        'Delivered 50+ successful projects with 4.9â˜… rating',
        'Generated $100K+ revenue through freelancing'
      ]
    }
  ];

  const bgClass = darkMode 
    ? 'bg-gradient-to-br from-gray-900 via-purple-900 to-gray-900' 
    : 'bg-gradient-to-br from-gray-50 via-purple-50 to-gray-50';

  const textClass = darkMode ? 'text-white' : 'text-gray-900';
  const cardBg = darkMode ? 'bg-gray-800/50' : 'bg-white/80';
  const borderColor = darkMode ? 'border-purple-500/30' : 'border-purple-300/50';

  return (
    <div className={`min-h-screen ${bgClass} ${textClass} relative overflow-hidden transition-all duration-500`}>
      {/* Cursor Glow Effect */}
      {darkMode && (
        <div 
          className="fixed w-96 h-96 rounded-full pointer-events-none z-50 opacity-30 blur-3xl transition-all duration-300"
          style={{
            background: 'radial-gradient(circle, rgba(147,51,234,0.6) 0%, transparent 70%)',
            left: mousePosition.x - 192,
            top: mousePosition.y - 192
          }}
        />
      )}

      {/* Animated Particles */}
      <div className="fixed inset-0 opacity-20 pointer-events-none">
